import{L as s}from"./Website.7d35b4d2.js";import{H as t,r,o,c as d,e as l,w as m,a,t as e,F as u,G as g}from"./app.b77cf987.js";import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import"./Footer.9a06d154.js";const c={layout:s,components:{Head:t},props:{title:Object}},b={class:"page-header parallaxie padding_top center-block"},k={class:"container"},h={class:"row"},y={class:"col-sm-12"},_={class:"page-titles text-center"},f={class:"whitecolor font-light bottom30"},j={class:"breadcrumb justify-content-center"},v=a("li",{class:"breadcrumb-item"},[a("a",{href:"/"},"Beranda")],-1),A={class:"breadcrumb-item active","aria-current":"page"},w=g('<section id="our-testimonial" class="padding_m"><div class="container"><div class="row"><div class="col-md-12 col-sm-12 text-center"><div class="heading-title bottom30"><span class="mb-3"><h2>Asosiasi Profesi Jabatan Fungsional SDM Aparatur</h2></span></div><div class="image hover-effect bottom30"><div><img alt="SEO" src="/assets/images/tentang.jpg"></div></div></div><div style="text-align:justify;"><p class="h6 bottom20 darkcolor mb-4">Atas berkat rahmat Tuhan Yang Maha Esa, bangsa Indonesia dalam mengisi kemerdekaan dapat hidup damai dalam tatanan global yang dinamis, dengan memanfaatkan seluruh sumber daya yang dimiliki guna mewujudkan masyarakat yang adil, makmur, dan sejahtera berdasarkan Pancasila dan Undang-Undang Dasar 1945. Untuk itu, diperlukan upaya pembaruan yang terus-menerus, baik secara perorangan, kelompok, maupun kerjasama yang didorong keyakinan serta tanggung jawab untuk ikut serta dalam meningkatkan kualitas aparatur sebagai pengawal pembangunan.</p><p class="h6 bottom20 darkcolor mb-4">Mengingat dinamika masyarakat Indonesia, paradigma pelayanan publik ditumbuhkembangkan agar dapat memenuhi kewajiban dasar aparatur dalam melaksanakan tugasnya. Jabatan fungsional Sumber Daya Manusia Aparatur (SDMA) sebagai insan Aparatur Pemerintah, ikut bertanggung jawab untuk mengembangkan melalui pengayaan ilmu pengetahuan dan teknologi yang selaras dengan nilai moral dan etika bangsa Indonesia, sehingga dalam pemberian pelayanan publik bersikap profesional. </p><p class="h6 bottom20 darkcolor mb-4">Untuk mengoptimalkan peran dalam menjawab tantangan, sumber daya manusia Indonesia harus memiliki etos kerja. Analis dan Pranata SDMA perlu bersatu dalam satu wadah organisasi profesi yang mandiri dan independen untuk ikut berpartisipasi dalam pembangunan Bangsa dan Negara, maka dibentuklah Organisasi Profesi yang diberi nama Asosiasi Profesi Jabatan Fungsional Sumber Daya Aparatur (Aspro SDMA) yang berdasarkan Pancasila dan Undang-Undang Dasar 1945. </p><p class="h6 bottom20 darkcolor mb-4">Melalui organisasi profesi, pejabat fungsional SDMA dapat tumbuh berkembang bersama menghadirkan pendekatan-pendekatan yang mutakhir serta mewujudkan sumber daya manusia aparatur yang kapabel dan berdaya saing. Organisasi profesi jabatan fungsional SDMA merupakan bagian yang tidak terpisahkan dari instansi pembina dalam hal ini Badan Kepegawaian Negara (BKN), oleh karena itu organisasi profesi ini bertujuan memberikan kontribusi sebesar-besarnya bagi pencapaian kinerja penyelenggaraan manajemen aparatur sipil negara dan sumber daya manusia aparatur lainnya. Diharapkan melalui wadah organisasi profesi para pejabat fungsional SDMA dapat berkiprah dengan rahmat Tuhan Yang Maha Esa serta didorong oleh keinginan luhur untuk mewujudkan cita-cita bangsa dalam membentuk Aparatur Sipil Negara yang profesional dan berintegritas, maka dalam pelaksanaannya akan ditentukan pada Anggaran Dasar yang ditetapkan dalam Musyawarah Aspro SDMA sebagai wadah tertinggi dalam menetapkan kebijakan organisasi. </p></div></div></div></section>',1);function D(M,S,n,x,B,N){const i=r("Head");return o(),d(u,null,[l(i,null,{default:m(()=>[a("title",null,e(n.title),1)]),_:1}),a("section",b,[a("div",k,[a("div",h,[a("div",y,[a("div",_,[a("h2",f,e(n.title),1),a("ul",j,[v,a("li",A,e(n.title),1)])])])])])]),w],64)}const H=p(c,[["render",D]]);export{H as default};
