import{L as t}from"./Website.8f064e42.js";import{H as i,r,o,c as l,e as d,w as c,a,t as n,F as m,G as p}from"./app.5846b9cb.js";import{_ as g}from"./_plugin-vue_export-helper.cdc0426e.js";import"./Footer.9276547d.js";const u={layout:t,components:{Head:i},props:{title:Object}},b={class:"page-header parallaxie padding_top center-block"},h={class:"container"},_={class:"row"},k={class:"col-sm-12"},A={class:"page-titles text-center"},f={class:"whitecolor font-light bottom30"},y={class:"breadcrumb justify-content-center"},v=a("li",{class:"breadcrumb-item"},[a("a",{href:"/"},"Beranda")],-1),I={class:"breadcrumb-item active","aria-current":"page"},w=p('<section id="our-testimonial" class="padding_m"><div class="container"><div class="row"><div class="col-md-12 col-sm-12 text-center"><div class="heading-title bottom30"><div class="image hover-effect bottom30"><div class="mt-3"><img alt="SEO" src="/assets/images/sejarah.jpg"></div></div></div><div style="text-align:justify;"><p class="h6 bottom20 darkcolor mb-4">Hadirnya Asosiasi Profesi Jabatan Fungsional Sumber Daya Manusia Aparatur pada tanggal 23 Oktober 2023 tidak terlepas dari semangat tranformasi dari Jabatan Fungsional Analis Kepegawaian menjadi Analis Sumber Daya Manusia Aparatur dan Pranata SDM Aparatur . Jabatan ini bernaung dalam Asosiasi Profesi bernama Ikatan Profesi Analis Kepegawaian Republik Indonesia atau IPAKRI yang lahir pada tanggal 15 Mei 2015. IPAKRI merupakan bentuk pemutakhiran dari Organisasi Profesi Analis Kepegawaian sebelumnya yaitu Ikatan Analis Kepegawaian Indonesia atau IAKI, yang lahir pada tanggal 13 September 2012. </p><p class="h6 bottom20 darkcolor mb-4">IPAKRI di bawah kepemimpinan H. Jono, S.Sos., M.M., telah menorehkan banyak kiprah dalam membangun profesionalisme para pejabat Analis Kepegawaian serta memberikan pengalaman berorganisasi bagi para pejabat fungsional Analis Kepegawaian. Bersama tujuh Kepengurusan Cabang, IPAKRI berhasil memberikan landasan yang kokoh bagi terbangunnya Aspro SDMA yang sebagaian besar memiliki nafas dan semangat serta filosofi yang sama sebagai Insan Kepegawaian. </p><p class="h6 bottom20 darkcolor mb-4">Aspro SDMA yang merupakan tranformasi dari IPAKRI merupakan langkah baru dalam upaya membangun organisasi yang dinamis dan sejalan dengan tuntutan jaman. Aspro SDMA merupakan langkah awal para Analis SDM Aparatur dan Pranata SDM Aparatur yang senantiasa terbuka pada goresan kiprah dan karya para anggotanya sebagai wujud sejarah organisasi ini di masa yang akan datang. </p></div></div></div></div></section>',1);function S(j,K,e,M,P,D){const s=r("Head");return o(),l(m,null,[d(s,null,{default:c(()=>[a("title",null,n(e.title),1)]),_:1}),a("section",b,[a("div",h,[a("div",_,[a("div",k,[a("div",A,[a("h2",f,n(e.title),1),a("ul",y,[v,a("li",I,n(e.title),1)])])])])])]),w],64)}const F=g(u,[["render",S]]);export{F as default};
